const drawMap = (newMapState = null) => {
  if (Array.isArray(newMapState)) {
    mapData = newMapState;
  }

  const hasTerritory = (territoryId) => mapData.some((territory) => territory.id === territoryId);
  if (selectedTerritoryId && !hasTerritory(selectedTerritoryId)) {
    selectedTerritoryId = null;
  }

  const selectedSource = mapData.find((territory) => territory.id === selectedSourceId);
  if (!selectedSource) {
    selectedSourceId = null;
    selectedTargetId = null;
  } else if (phase === 'TURN_ATTACK') {
    const canAttackFromSource = selectedSource.ownerId === socket.id && (selectedSource.troops || 0) > 1;
    if (!canAttackFromSource) {
      selectedSourceId = null;
      selectedTargetId = null;
    }
  }

  const hasOwnedPath = (playerId, fromId, toId) => {
    if (!fromId || !toId) return false;
    const owned = new Set(
      mapData.filter((territory) => territory.ownerId === playerId).map((territory) => territory.id)
    );
    if (!owned.has(fromId) || !owned.has(toId)) return false;
    const visited = new Set([fromId]);
    const queue = [fromId];
    while (queue.length) {
      const currentId = queue.shift();
      if (currentId === toId) return true;
      const current = mapData.find((territory) => territory.id === currentId);
      if (!current) continue;
      for (const neighborId of current.neighbors || []) {
        if (!owned.has(neighborId) || visited.has(neighborId)) continue;
        visited.add(neighborId);
        queue.push(neighborId);
      }
    }
    return false;
  };

  if (selectedSourceId && selectedTargetId) {
    const source = mapData.find((territory) => territory.id === selectedSourceId);
    const target = mapData.find((territory) => territory.id === selectedTargetId);
    let isValidTarget = false;
    if (phase === 'TURN_ATTACK') {
      isValidTarget =
        !!source &&
        !!target &&
        source.ownerId === socket.id &&
        target.ownerId !== socket.id &&
        (source.neighbors || []).includes(target.id);
    } else if (phase === 'TURN_FORTIFY') {
      isValidTarget =
        !!source &&
        !!target &&
        source.id !== target.id &&
        source.ownerId === socket.id &&
        target.ownerId === socket.id &&
        hasOwnedPath(socket.id, source.id, target.id);
    } else {
      isValidTarget = !!source && !!target;
    }
    if (!isValidTarget) {
      selectedTargetId = null;
    }
  } else if (selectedTargetId && !hasTerritory(selectedTargetId)) {
    selectedTargetId = null;
  }

  ensureSvgLayers();
  fitViewBoxToMap();
  updatePathStyles();
  updateLabels();
};

const faceToRotation = (face) => {
  if (face === 1) return { x: 0, y: 0 };
  if (face === 2) return { x: 0, y: -90 };
  if (face === 3) return { x: -90, y: 0 };
  if (face === 4) return { x: 90, y: 0 };
  if (face === 5) return { x: 0, y: 90 };
  return { x: 0, y: 180 };
};

const createDieElement = (value, sideClass) => {
  const dieEl = document.createElement('div');
  dieEl.className = `die ${sideClass} rolling`;
  dieEl.dataset.value = String(value);

  for (let face = 1; face <= 6; face += 1) {
    const faceEl = document.createElement('div');
    faceEl.className = 'die-face';
    faceEl.textContent = String(face);
    dieEl.appendChild(faceEl);
  }

  return dieEl;
};

const stopDieAtValue = (dieEl, value) => {
  const boundedValue = Math.max(1, Math.min(6, Number(value) || 1));
  const final = faceToRotation(boundedValue);
  const spinX = 720 + Math.floor(Math.random() * 2) * 360;
  const spinY = 720 + Math.floor(Math.random() * 2) * 360;
  dieEl.classList.remove('rolling');
  dieEl.style.transition = 'transform 450ms cubic-bezier(0.22, 1, 0.36, 1)';
  dieEl.style.transform = `rotateX(${spinX + final.x}deg) rotateY(${spinY + final.y}deg)`;
};

const renderDiceSide = (label, rolls, sideClass) => {
  const rowEl = document.createElement('div');
  rowEl.className = 'dice-side';

  const labelEl = document.createElement('div');
  labelEl.className = 'dice-label';
  labelEl.textContent = label;
  rowEl.appendChild(labelEl);

  const groupEl = document.createElement('div');
  groupEl.className = 'dice-group';

  (rolls || []).forEach((rollValue) => {
    const dieEl = createDieElement(rollValue, sideClass);
    groupEl.appendChild(dieEl);
  });

  rowEl.appendChild(groupEl);
  return rowEl;
};

