const openBattleReadyOverlay = (payload) => {
  if (!battleOverlayEl || !battleDiceContainerEl) return;
  battlePending = true;
  rollRequested = false;
  battleContext = payload || null;
  battleDiceContainerEl.innerHTML = '';
  battleOverlayEl.classList.add('active');

  const isAttacker = payload && payload.attackerId === socket.id;
  const isDefender = payload && payload.defenderId === socket.id;
  if (isAttacker) {
    setBattleOverlayState({
      title: 'Battle Ready',
      showRollButton: true,
    });
    return;
  }
  if (isDefender) {
    setBattleOverlayState({
      title: 'Battle Ready',
      message: 'Esperando al atacante...',
    });
    return;
  }
  setBattleOverlayState({
    title: 'Battle Ready',
    message: 'Combate en preparacion...',
  });
};

const animateBattle = (attackerRolls, defenderRolls, onComplete) => {
  if (!battleOverlayEl || !battleDiceContainerEl || !battleOverlayStateEl) {
    if (typeof onComplete === 'function') onComplete();
    return;
  }
  if (battleTimeoutId) {
    window.clearTimeout(battleTimeoutId);
    battleTimeoutId = null;
  }
  if (revealTimeoutId) {
    window.clearTimeout(revealTimeoutId);
    revealTimeoutId = null;
  }

  battlePending = false;
  battleAnimationInProgress = true;
  rollRequested = false;
  battleDiceContainerEl.innerHTML = '';
  setBattleOverlayState({
    title: 'Resolving Battle',
    message: 'Rodando dados...',
  });
  battleDiceContainerEl.appendChild(renderDiceSide('Attacker', attackerRolls, 'attacker'));
  battleDiceContainerEl.appendChild(renderDiceSide('Defender', defenderRolls, 'defender'));
  battleOverlayEl.classList.add('active');
  SoundManager.startDiceRoll();

  const allDice = Array.from(battleDiceContainerEl.querySelectorAll('.die'));
  battleTimeoutId = window.setTimeout(() => {
    allDice.forEach((dieEl) => {
      const value = Number(dieEl.dataset.value) || 1;
      stopDieAtValue(dieEl, value);
    });
    SoundManager.stopDiceRoll();
    battleTimeoutId = null;
  }, 2000);

  revealTimeoutId = window.setTimeout(() => {
    SoundManager.stopDiceRoll();
    battleOverlayEl.classList.remove('active');
    battleDiceContainerEl.innerHTML = '';
    battleOverlayStateEl.innerHTML = '';
    battleAnimationInProgress = false;
    battleContext = null;
    revealTimeoutId = null;
    if (typeof onComplete === 'function') onComplete();
  }, 3000);
};

